import { useEffect, useState } from "react"
import { IoChevronDownSharp, IoChevronUpSharp } from 'react-icons/io5'
import * as Styled from "./styles"

export const PetImages = ({images})=>{

    const firstImg = images ? images[0] : ''
    
    const [ slider, setSlider ] = useState(0)
    const [ mainImg, setMainImg ] = useState(firstImg)

    
    const lengthImg = images ? images.length : 0

    const maxSlider = images ? (lengthImg-3)*112 : ''

    const handleUpArrowClick = ()=>{
        if(slider > 0){
            setSlider(r => r-112)
        }

    }

    const handleDownArrowClick = ()=>{
        
        if(slider < maxSlider){
            setSlider(r => r+112)
        }
    }

    const handleOnPreviewImageClick = (img)=>{

        setMainImg(img)
    }
    
    return(
        <Styled.ImagesContainer>
            <Styled.PreviewImageContainer>
                { lengthImg > 3 ? <Styled.Arrows><IoChevronUpSharp onClick={handleUpArrowClick} /></Styled.Arrows> : ''}
                <Styled.Images>
                    <Styled.ImageSlider slider={slider}>
                        {images ? images.map((img, index)=> <img key={index} onClick={()=>handleOnPreviewImageClick(img)} src={`http://localhost:5000/images/pets/${img}`} alt={img} />) : ''}
                    </Styled.ImageSlider>
                </Styled.Images>
                { lengthImg > 3 ? <Styled.Arrows onClick={handleDownArrowClick} ><IoChevronDownSharp/></Styled.Arrows> : ''}
            </Styled.PreviewImageContainer>
            <Styled.MainImage>
                <img src={`http://localhost:5000/images/pets/${mainImg}`} alt="" />
            </Styled.MainImage>
        </Styled.ImagesContainer>
    )

}
