import { useState, useEffect } from 'react'
import { useGetApi} from '../../hooks/useGetApi'


import { IoEyeOutline, IoEyeOffOutline } from 'react-icons/io5'
import PatasImg from '../../images/patas.png'
import { Input } from '../../components/Form/Input'

import * as Styled from './styles'

export const Profile = ()=>{

    const [user, setUser] = useState({
        name : '',
        phone : '',
        email : '',
    })
    const [showPassword, setShowPassword] = useState(false)
    
    const [getCheckUser] = useGetApi()

    /* const handleChange = (e)=>{




    } */

    useEffect(()=>{
        getCheckUser().then(r => setUser(r))
        console.log('render')
    }, [ getCheckUser])

    return(
        <Styled.Container>
            <Styled.ContainerEdit>
                <h1>Editar usuário</h1>
                <form >
                    <Input text='Nome' type='text' name='name' placeholder='Digite seu nome'  value={user.name} />
                    <Input text='Telefone' type='text' name='phone' placeholder='Digite o seu telefone'  value={user.phone}/>
                    <Input text='Email' type='email' name='email' placeholder='Digite seu email'  value={`${user.email}`}/>
                    <Input text='Senha' type={showPassword? 'text' : 'password'} name='password' placeholder='Digite uma senha'/>
                    <Input text='Confirmação de senha ' type = {showPassword? 'text' : 'password'} name='confirmpassword' placeholder='Confirme a senha' />
                    <Input text='Imagem ' type = 'file' name='image' />
                    {showPassword? <IoEyeOutline onClick={()=>{setShowPassword(!showPassword)}} className='passIcon'/> : <IoEyeOffOutline className='passIcon' onClick={()=>{setShowPassword(!showPassword)}}/>}
                    <input type="submit" value='Salvar'/>
                </form>
            
            </Styled.ContainerEdit>
            <img src={PatasImg} alt='patas' />
        </Styled.Container>
    )
}
