import styled from "styled-components";

export const CardContainer = styled.div` 

    width: 260px;
    height: 300px;
    background-color: #be7abb;
    margin-left: 20px;
    margin-top: 20px;
    border-radius: 3px;
    display: flex;
    justify-content: space-around;
    flex-direction: row;
    flex-wrap: wrap;


`
