import styled from "styled-components"


export const Card = styled.div` 

    width: 240px;
    height: 320px;
    background: linear-gradient(90deg, rgba(241,0,142,0.5) 0%, rgba(255,244,56,0.4) 100%);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-around;
    border-radius: 20px;

    img{
        width: 150px;
        height: 150px;
        border-radius: 9999px;
    }

    h2{
        color: #f1008e;
    }

    
`

export const ButtonContainer = styled.div` 



`
