import EventEmitter from "events";


export const Event =  new EventEmitter()
 


export const useFlashMessage =()=>{

    

    const setFlashMessage =(msg, type)=>{

       Event.emit('flash', {
            message : msg,
            type : type,

        })
    }


    return { setFlashMessage, Event }
}
