import styled from "styled-components";


export const PetContainer = styled.section` 

    width: 70%;
    height: 520px;
    margin: 10px auto;
    display: flex;
    justify-content: space-between;
    border: 1px solid blue;

    div{
        width: auto;
    }
`

export const PetInformation = styled.section` 

    border: 1px solid red;
    width: 480px;
    height: 520px;
`
