import styled from "styled-components";



export const Nav = styled.nav` 

    display: flex;
    justify-content: space-between;
    padding: 1em 1.5em;
    background-color: #d02090;
    color: #ff1493;
    font-weight: bold;
    

`

export const Logo = styled.div` 

    display: flex;
    align-items: center;

    img{
        width: 40px;
        margin-right: 0.8em;
    }



`

export const Menu = styled.ul` 

    display: flex;
    align-items: center;
    list-style: none;



`
