import styled, { css } from "styled-components";



export const Container = styled.div`

    position: absolute;
    z-index: 1000;
    width: 100%;
    height: 100vh;
    background: #111;

    ${({isActived})=> css`
    
        ${isActived ? '' : 'display: none;'}

    
    `}



`



export const Card = styled.div`  

    width: 200px;
    height: 200px;
    background: red;
    margin: 0 auto;
    position: absolute;
    top: 50%;
    margin-left: -100px;
    left: 50%;
    z-index: 10001;

    ${({isActived2})=> css`
    
        ${isActived2 ? '' : 'display: none;'}

    
    `}

`
