import { useState, useEffect } from 'react'
import * as Styled from './styles'
import { IoChevronForwardSharp, IoChevronBackSharp } from 'react-icons/io5'
import { usePet } from '../../hooks/usePet'

export const Home = ()=> {

  const [ slider, setSlider ] = useState(0)
  const [ newPets, setNewPets ] = useState('')

  const { getNewPets } = usePet()

  useEffect(()=>{
    getNewPets().then((res)=>{ setNewPets(res)})
  }, [getNewPets])

  const handleOnRightClick = ()=>{
    if(slider < 600*newPets.length){
      setSlider( r => r+600)
    }
  }

  const handleOnLeftClick = ()=>{

    if(slider > 0){
      setSlider( r => r-600)
    }
  }

  return(

    <Styled.Container>
        <Styled.SliderContainer>
          { newPets.length > 0 ? 
          newPets.map( pet => <Styled.NewPetContainer slider={slider} img={`url(http://localhost:5000/images/pets/${pet.images[0]})`} key={pet._id}><h1>{pet.name}, {pet.age} {pet.age > 1 ? 'anos' : 'ano'}</h1></Styled.NewPetContainer>) 
          :
          <p>Não há pets</p>

          }
        </Styled.SliderContainer>
        <IoChevronForwardSharp onClick={handleOnRightClick} className='right-arrow'/>
        <IoChevronBackSharp onClick={handleOnLeftClick} className='left-arrow'/>
    </Styled.Container>

  )

}
