import styled from "styled-components";


export const Message = styled.div` 

    position: absolute;
    width: 300px;
    text-align: center;
    padding: 1em;
    border: 1px solid #fff;
    border-radius: 10px;
    left: 0;
    right: 0;
    margin-top: 15px;
    margin-left: auto;
    margin-right: auto;


`
