import EventEmitter from "events";



 


export const useFlashMessage =()=>{

    const Event =  new EventEmitter()

    const setFlashMessage =(msg, type)=>{

       Event.emit('flash', {
            message : msg,
            type : type,

        })
    }


    return { setFlashMessage, Event }
}
