import styled, {css} from "styled-components";


export const Message = styled.div` 

    position: absolute;
    width: 500px;
    text-align: center;
    background-color: #fff;
    padding: 1em;
    border: 1px solid #fff;
    border-radius: 20px 3px 20px 3px;
    left: 0;
    right: 0;
    margin-top: 30px;
    margin-left: auto;
    margin-right: auto;

    .success{
        color: #155724;
    }

    .error{
        color: #721c24
    }

    ${({type})=> css`

        ${type === 'success' ? 'color : #155724' : 'color: #721c24'}
    
    
    `}

`
