import styled from "styled-components"


export const Card = styled.div` 

    width: 240px;
    height: 320px;
    background-color: #e1e1e1;

    img{
        width: 150px;
        height: 150px;
        border-radius: 9999px;
    }

    a, button{
        text-decoration: none;
        margin: 1px solid black;
    }
`
