import styled from "styled-components";

export const CardContainer = styled.a` 

    width: 260px;
    height: 300px;
    background-color: #be7abb;
    margin-left: 10px;
    margin-right: 10px;
    margin-top: 20px;
    border-radius: 3px;
    cursor: pointer;
    transform: scale(0.9);
    &:hover{
        transform: scale(1);
    }
    transition: all ease-in-out 0.2s;

    img{
        width: 140px;
        height: 140px;
        border-radius: 9999px;
    }

`
