import styled from "styled-components"


export const Card = styled.div` 

    width: 240px;
    height: 320px;
    background: linear-gradient(90deg, rgba(241,0,142,0.5) 0%, rgba(255,244,56,0.4) 100%);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-around;
    border-radius: 20px;

    img{
        width: 150px;
        height: 150px;
        border-radius: 9999px;
    }

    h2{
        color: #f1008e;
    }

    
`

export const ButtonContainer = styled.div` 

    display: flex;
    justify-content: space-between;

    button{
        margin-left: 20px;
        width: 60px;
        height: 30px;
        border: none;
        border-radius: 5px;
        background-color: red;
        font-weight: bold;
        color: #fff;
        cursor: pointer;
    }
    button:hover{
        opacity: 0.8;
    }

    a{
        width: 60px;
        height: 30px;
        color: #fff;
        text-decoration: none;
        text-align: center;
        background-color: red;
    }


`
