import { IoChevronForward, IoChevronBack } from 'react-icons/io5'
import * as Styled from './styles'


export const Pagination = ({numberOfPages, nextPage, page, setPage})=>{

    const maxButtons = 9
    const maxLeftButtons = (maxButtons-1)/2
    const firstButton = Math.max(page - maxLeftButtons, 1)


    const handleLeftArrowClick = ()=>{
        if(page > 1){
            setPage(page-1)
        }
    }
    const handleRightArrowClick = ()=>{
        if(page < numberOfPages){
            setPage(page-1)
        }
    }

    return(
        <Styled.Ul>
            <li><button onClick={handleLeftArrowClick}><IoChevronBack/></button></li>
            {Array.from({ length : maxButtons })
                .map((_, index)=> index+firstButton <= numberOfPages ? <li key={index}><button onClick={()=>setPage(index+firstButton)}>{index+firstButton}</button></li> : ''
            )}
            <li><button onClick={()=>setPage(page+1)}><IoChevronForward/></button></li>
        </Styled.Ul>
    )
}
