import styled from 'styled-components'

export const MyAdoptions = styled.section` 

    h1{
        color: #f1008e;
        margin-left: 20px;
        margin-top: 20px;
    }

`


export const MyAdoptionsContainer = styled.section` 

    padding: 20px;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;

    p{
        font-size: 1.2em;
        font-weight: bold;
        color: #f6586f;
    }

`
