import styled from "styled-components";


export const CardContainer = styled.div` 

    position: relative;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: #000;
    opacity: 0.5;


`

export const CardContact = styled.div` 

    width: 120px;
    height: 100px;
    position: absolute;
    margin-left: auto;
    margin-right: auto;
    left: 0;
    right: 0;
    text-align: center;
    background-color: green;
`
