    }
    
    return(
        <Styled.MyPets>
            <h1>Meus Pets:</h1>
            <Styled.MyPetsContainer>
                
                { pets.length > 0 ? (
                    pets.map(pet => <PetCardMypets key={pet._id} pet={pet}/>)
                ) 
                :
                <p>'Nenhum pet'</p>
                }
                
            </Styled.MyPetsContainer>
        </Styled.MyPets>
    )
}
