import { useState, useEffect } from 'react'
import * as Styled from './styles'
import { usePet } from '../../hooks/usePet'

export const Home = ()=> {

  const [ newPets, setNewPets ] = useState('')

  const { getNewPets } = usePet()

  useEffect(()=>{
    getNewPets().then((res)=>{ setNewPets(res)})
  }, [getNewPets])

  return(
    <>

      <Styled.SliderContainer>
        { newPets.length > 0 ? 
        newPets.map( pet => <Styled.NewPetContainer key={pet._id}><h1>{pet.name}</h1></Styled.NewPetContainer>) 
        :
        <p>Não há pets</p>

        }
      </Styled.SliderContainer>

    </>



  )

}
