import api from '../helpers/api'



export const useGetApi= ()=>{


    const getCheckUser = async(token)=>{
        api.get('/users/checkuser', {
            headers: {
                Authorization: `Bearer ${JSON.parse(token)}`
            }
        })
    }

    
    return [getCheckUser]

}
