import styled, {css} from "styled-components"

export const Container = styled.div` 
    width: 600px;
    height: 600px;
    overflow: hidden;
    margin: 0 auto;
    position: relative;


    .right-arrow{
        font-size: 40px;
        color: #fb008e;
        position: absolute;
        top: 45%;
        right: 5px;
        cursor: pointer;
    }
    .left-arrow{
        font-size: 40px;
        color: #fb008e;
        position: absolute;
        top: 45%;
        left: 5px;
        cursor: pointer;
    }


`

export const SliderContainer = styled.div` 
    width: 100000000px;
    height: 600px;
    overflow: hidden;
    display: flex;
    ${({slider})=> css` 
        margin-left: ${-slider};
    `}
    

`

export const NewPetContainer = styled.a`
    width: 600px;
    height: 600px;
    background-color: #f1f1f1;
    ${({img})=> css` 
       background-image: ${img};
    `}
    
    background-size: cover;

`
