import styled from "styled-components";


export const CardContainer = styled.div` 

    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: #000;
    opacity: 0.6;


`

export const CardContact = styled.div` 

    width: 500px;
    color: #1ED604;
    height: 100px;
    position: absolute;
    margin-left: auto;
    margin-right: auto;
    left: 0;
    right: 0;
    background-color: #fff;
    font-size: 10px;
    opacity: 1;
`
