import { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import {usePet } from '../../hooks/usePet'
import * as Styled from './styles'



export const PetCardMypets = ({pet, onClick})=>{

    const { concludeAdoption } = usePet()

    const [ petData, setPet ] = useState('')
    
    useEffect(()=>{
        setPet(pet)
    }, [petData])

    const handleOnConcludeButtonClick = ()=>{
        concludeAdoption(petData._id)
    }

    console.log(pet)
    return (
        <Styled.Card>
            <img src={`${process.env.REACT_APP_API}/images/pets/${petData.images[0]}`} alt={petData.name} />

            <h2>{petData.name}</h2>

            <p>Idade: {petData.age} {petData.age > 1 ? 'Anos' : 'Ano'}</p>

            {petData.available ?
                <button onClick={handleOnConcludeButtonClick}>Concluir adoção</button>
                :
                <h3>Adotado!</h3>
            }
            <Styled.ButtonContainer>
                <Link to={`/editpet/${petData._id}`}>Editar</Link>
                <button onClick={()=>onClick(petData._id)}>Excluir</button>
            </Styled.ButtonContainer>
        </Styled.Card>
    )
}
