import styled from "styled-components";

export const Container = styled.div` 


    min-height: 100vh;
    padding-top: 10%;
    background: linear-gradient(90deg, rgba(241,0,142,1) 0%, rgba(255,244,56,0.7959558823529411) 100%);
    display: flex;
    flex-direction: column;
    align-items: center;

    img{
        margin-top: -250px;
        margin-left: 400px;
        z-index: 1;
    }


`

export const ContainerRegister = styled.section` 

    
    width: 500px;
    padding: 20px;
    border-radius: 20px 3px 20px 3px;
    background-color: #fff;

    h1{
        width: 100%;
        text-align: center;
        color: #f1008e;
        margin-top: 10px;
        margin-bottom: 20px;
    }

`
