import api from '../helpers/api'



export const useGetApi= ()=>{


    const getCheckUser = async()=>{
        const dataRes = await api.get('/users/checkuser', {
            headers: {
                Authorization: `Bearer ${JSON.parse(localStorage.getItem('token'))}`
            }
        })

        return dataRes.data
    }

    
    return [getCheckUser]

}
