import styled from "styled-components";


export const PetContainer = styled.section` 

    width: 70%;
    height: 520px;
    margin: 10px auto;
    display: flex;
    justify-content: space-between;

`

export const PetInformationContainer = styled.section` 
    width: 480px;
    height: 520px;
    display: flex;
    justify-content: space-around;
    flex-direction: column;

    h1{
        font-size: 2em;
        color: #f6586f;
        align-self: flex-start;
    }

    button{
        width: 200px;
        height: 40px;
        border: none;
        border-radius: 9999px;
        background-color: #09F90D;
        color: #fff;
        font-size: 1em;
        font-weight: bold;
        cursor: pointer;
        align-self: center;
    }

    button:hover{
        background-color: #6EFA55;
    }

`

export const PetInformation = styled.div` 

    display: flex;
    flex-wrap: wrap;


    p{
        padding: 2px;
        font-size: 1.3em;
        font-weight: 600;
        color: #FA9BEF;
        margin-right: 20px;
    
    }

    p span{
        color: #ED48C3;
    }
    
`
